import React, {useState, useEffect} from 'react';
import {Form, Input, Button , Card, Icon, Avatar} from 'antd';
import PostForm from "../components/PostForm";
import PostCard from "../components/PostCard";
import {useDispatch} from "react-redux";
import {LOG_IN, logoutAction} from "../reducers/user";

{/*  TODO 서버에서 받아야할 더미데이터  */}
const dummy={
    isLoggedIn : true,
    imagePaths : [],
    mainPosts : [
        //첫번째 유저의 포스팅
        {
            User:{
                id:1,
                nickname:'제로초',
            },
            content:'첫번 째 게시글',
            img: 'https://i.pinimg.com/originals/d9/82/f4/d982f4ec7d06f6910539472634e1f9b1.png'
        }
    ],
};
const Home = () => {
    //액션을 리덕스 훅스 문법으로 디스팻치하는 방법
    const dispatch = useDispatch();
    //스테이트 가져옴
    const user = useSelector (state => state.user); //리덕스 스토어 (index.js)전체스테이트에서 유저를 가져와라
    console.log(user);
    useEffect(()=> {
            dispatch(
                {
                    type : LOG_IN,
                    data:{
                        nickname:'홍길동'
                    },
                }
            );
            dispatch(logoutAction);

        }, []);
    return (
        <div>
            {user ? <div>로그인 했습니다 {user.nickname}</div> : <div>로그아웃 했습니다. </div>}
            {/* 포스팅 입력 폼  */}
            {dummy.isLoggedIn && <PostForm/>}
            {/* 메인 포스트 매핑  */}
            {dummy.mainPosts.map((c) => {
                return(
                    <PostCard key={c} post={c}/>
                )
          })}
        </div>
    );
};

export default Home;
