import React, { useCallback, useState } from 'react';
import {Menu, Input, Button, Row, Col, Card, Avatar, Checkbox, Form} from 'antd';
import Link from 'next/link'
import PropTypes from 'prop-types';
//import { useInput } from '../pages/signup';

const LoginForm = () => {
    //커스텀 훅 (export : 외부로 보내기)
    //만약 이곳에 값이 전달됬을경우
    const useInput = (initValue = null) => {
        //전달된 매개변수를 value, setter로 스테이트 화 시킵니다
        const [value, setter] = useState(initValue);
        //핸들러라는 이름의 변수에  setter의 value를 넣습니다.
        const handler = useCallback((e) => {
            setter(e.target.value);
        },[]);
        return [value, handler];

    };

    //스테이트
    const [id, onChangeId] = useInput('');
    const [password, onChangePassword] = useInput('');
    //로그인 버튼 클릭
    const onSubmitform = useCallback((e) => {
        e.preventDefault();
        console.log(
            {id, password}
        );
    },[]);


    return (
        <Form onSubmit = {onSubmitform}>
            {/* 로그인 폼 */}
            <div>
                <label htmlFor = "user-id">아이디</label>
                <br />
                <Input name="user-id" value={id} onChange={onChangeId} required />
            </div>
            <div>
                <label htmlFor = "user-password">비밀번호</label>
                <br />
                <Input name="user-password" value={password} onChange={onChangePassword} type="password"  />
            </div>
            <div style={{ margin:10 }}>
                <Button type="primary" htmlType="submit" loading={false}>로그인</Button>
            </div>
        </Form>

    );
};


export default LoginForm;
