import React from 'react';
import {Menu, Input, Button, Row, Col, Card, Avatar, Checkbox, Form} from 'antd';
import Link from 'next/link'
import PropTypes from 'prop-types';
import LoginForm from './LoginForm'

const dummy = {
    nickname : '제로초',
    post : [],
    followers : [],
    followings : [],
    isLoggedIn : false
};

const AppLayout = ( {children} ) => {
    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{ verticalAlign: 'middle'}}/>
                </Menu.Item>
            </Menu>
            <Button><Link href="/signup"><a>회원가입</a></Link></Button>
            <Row>
                {/* xs : 모바일, md: 중간 화면, lg: 큰 화면, sm: 작은화면 */}
                <Col xs={ 24 } md={ 6 } >
                   {/* 삼항 연산자 : 로그인여부에 따라 다른 div 출력 */}
                    {dummy.isLoggedIn ? <Card
                           actions={[
                                <div key="twit">포스트 <br/> {dummy.post.length}</div>,
                               <div key="following">팔로잉 <br/> {dummy.followings.length}</div>,
                               <div key="follower">팔로 <br/> {dummy.followers.length}</div>,
                           ]
                           }>
                        {/* Avatar 태그 : 썸네일 antd 컴포 */}
                        <Card.Meta
                            avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                            title={dummy.nickname}
                        />
                    </Card>
                    :
                    <LoginForm />
                    }

                </Col>

                <Col xs={ 24 } md={ 12 } >
                    {children}
                </Col>
                <Col xs={ 24 } md={ 6 } >세번ㅐ</Col>
            </Row>
        </div>
    );
};

/*Compoent 타입 체크
AppLayout.PropTypes = {
    children : PropTypes.node,
};

*/
export default AppLayout;
