import React from 'react';
import {Menu, Input, Button, Row, Col, Card, Avatar, Checkbox, Form} from 'antd';
import Link from 'next/link'
import PropTypes from 'prop-types';
import LoginForm from './LoginForm';
import UserPofile from './UserProfile';

const dummy = {
    nickname : '제로초',
    post : [],
    followers : [],
    followings : [],
    isLoggedIn : false
};

const AppLayout = ( {children} ) => {
    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>노드버드</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{ verticalAlign: 'middle'}}/>
                </Menu.Item>
            </Menu>
            <Button><Link href="/signup"><a>회원가입</a></Link></Button>
            <Row>
                {/* xs : 모바일, md: 중간 화면, lg: 큰 화면, sm: 작은화면 */}
                <Col xs={ 24 } md={ 6 } >
                   {/* 삼항 연산자 : 로그인여부에 따라 다른 div 출력 */}
                    {dummy.isLoggedIn ? <UserProfile />
                    :
                    <LoginForm />
                    }

                </Col>

                <Col xs={ 24 } md={ 12 } >
                    {children}
                </Col>
                <Col xs={ 24 } md={ 6 } >세번째</Col>
            </Row>
        </div>
    );
};

/*Compoent 타입 체크
AppLayout.PropTypes = {
    children : PropTypes.node,
};

*/
export default AppLayout;
