import { all, fork, put, takeLatest, call } from 'redux-saga/effects'
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE } from "../reducers/user";

/*

로그인 관련 사가 제너레이터

-사가 이펙트 정리-
take : 다른 사가를 기다림.
takeLatest :  해당 사가 실행완료 될때 까지 기다렸다가 액션 실행
*/

//서버에 로그인 요청
function loginApi() {

}
//로그인 액션 실행
function* login() {
    try{
        yield call(loginApi());
        //서버에 요청해서 성공하면 pit(distpach와 동일)
        yield put({
            type: LOG_IN_SUCCESS
        })
    }catch (e) {
        //서버 요청 실패시
        console.error(e);
        yield put({
            type: LOG_IN_FAILURE
        })
    }
}

//로그인이라는 액션을 기다리고
//LOG_IN 이라는 타입으 액션이 실행될때까지 기다렸다가 login 함수 실
function* watchLogin() {
    yield takeLatest(LOG_IN, login)
}

function* helloSaga() {
    console.log('hello saga - login.js')
}
export default function* userSaga() {
    yield all([
        //fork(watchLogin),
        //helloSaga(),
    ]);
}
