import { all, fork, takeLatest, put, delay } from 'redux-saga/effects';
import { ADD_POST_FAILURE, ADD_POST_REQUEST, ADD_POST_SUCCESS } from '../reducers/post';
import { ADD_COMMENT_FAILURE, ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS } from '../reducers/post';
/*
 사가 구성 3종 세트
 api 콜 함수 - 실행(실제 동작) 함수 - watch 함수
*/

axios.default.baseURL = 'http://localhost:3065/api'
function addPostAPI() {
    return axios.post('/post', postData,{
        withCredentials: true //로그인한 사람만 작성 가능하므로 쿠키로 인증 필요
    })
}

function* addPost() {
    try {
        yield call(addPostAPI, action.data)
        yield put({
          type: ADD_POST_SUCCESS,
        });
    } catch (e) {
        yield put({
          type: ADD_POST_FAILURE,
          error: e,
        });
    }
}

function* watchAddPost() {
  yield takeLatest(ADD_POST_REQUEST, addPost);
}

function addCommentAPI() {

}

function* addComment(action) {
  try {
    yield delay(2000);
    yield put({
      type: ADD_COMMENT_SUCCESS,
      data: {
        postId: action.data.postId,
      },
    });
  } catch (e) {
    yield put({
      type: ADD_COMMENT_FAILURE,
      error: e,
    });
  }
}

function* watchAddComment() {
  yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}

export default function* postSaga() {
  yield all([
    fork(watchAddPost),
    fork(watchAddComment),
  ]);
}
